import { useTranslation } from 'react-i18next';
import React from 'react';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Skeleton from '@mui/material/Skeleton';
import Stack from '@mui/material/Stack';

import { CardIsLockedOverlay, CardWrapper, CardInner, CardFront, CardBack } from './NftCard.styles';
import GradientButton from '../../../components/GradientButton/GradientButton';

const DEFAULT_IMAGE = {
  alt: '',
  title: '',
  src: 'data:image/png;base64,',
};

const IMAGE_MODEL = {
  alt: '',
  title: '',
  src: '',
};

const NftCard = ({ title = '', level = 1, image = IMAGE_MODEL, score = 0, isLocked = true, unlockedDate = '', isLoading = false }) => {
  const { t } = useTranslation();

  if (isLoading) { return <MockCard />; }

  return (
    <CardWrapper isLocked={isLocked || !unlockedDate}>
      <CardInner>
        <CardFront>
          <Card>
            <CardMedia
              component="img"
              height="225"
              image={image.src || DEFAULT_IMAGE.src}
            />
            <CardContent>
              <Stack>
                <Typography gutterBottom variant="subtitle1" component="div">
                  {title}
                </Typography>
                <Stack direction="row" alignItems="baseline" justifyContent="space-between" gap={0.5}>
                  <Typography variant="h4" color="text.primary" component="div">
                    {t('meshNftsCarousel.card.level', { level })}
                  </Typography>
                  <Typography variant="subtitle1" color="text.primary" component="div" sx={{ flexShrink: 0 }}>
                    {t('meshNftsCarousel.card.score', { score })}
                  </Typography>
                </Stack>
              </Stack>
            </CardContent>
          </Card>
          {isLocked && (
            <CardIsLockedOverlay>
              <GradientButton sx={{ width: 100 }}>{t('meshNftsCarousel.card.controls.unlock', { score })}</GradientButton>
            </CardIsLockedOverlay>
          )}
        </CardFront>
        {!(isLocked && !unlockedDate) && (
          <CardBack>
            <Card>
              <CardContent>
                <Stack direction="column" justifyContent="center" alignItems="center" gap={0.7} sx={{ height: '100%' }}>
                  <Typography variant="body1" color="text.primary" component="div" align="center">
                    {t('meshNftsCarousel.card.earnedDate')}
                  </Typography>
                  <Typography variant="body1" color="text.primary" component="div" align="center" sx={{ fontSize: '1.15rem', textTransform: 'uppercase' }}>
                    {t('intlDateTime', {
                      val: unlockedDate,
                      formatParams: {
                        val: { year: 'numeric', month: 'long', day: 'numeric' },
                      },
                    })}
                  </Typography>
                </Stack>
              </CardContent>
            </Card>
          </CardBack>
        )}
      </CardInner>
    </CardWrapper>
  );
};

const MockCard = () => (
  <CardWrapper isLocked>
    <CardInner>
      <CardFront>
        <Card>
          <Skeleton variant="rounded" height={225} />
          <CardContent sx={{ background: 'transparent !important' }}>
            <Stack>
              <Typography gutterBottom variant="subtitle1" component="div"><Skeleton width="8em" /></Typography>
              <Stack direction="row" alignItems="baseline" justifyContent="space-between">
                <Typography variant="h4" component="div"><Skeleton width="3em" /></Typography>
                <Typography variant="subtitle1" component="div"><Skeleton width="2em" /></Typography>
              </Stack>
            </Stack>
          </CardContent>
        </Card>
      </CardFront>
    </CardInner>
  </CardWrapper>
);

NftCard.propTypes = {
  title: PropTypes.string,
  level: PropTypes.number,
  image: PropTypes.shape({
    alt: PropTypes.string,
    title: PropTypes.string,
    src: PropTypes.string,
  }),
  score: PropTypes.number,
  unlockedDate: PropTypes.number,
  isLocked: PropTypes.bool,
  isLoading: PropTypes.bool,
};

export default NftCard;
